mapper.select:
select id, owner, title, description, teachers, courseTime, courseOver, infoPdf, prePdf, preVideo
from tb_course

    where ${page.searchType} like concat('%', #{page.searchContent}, '%')

 order by ${page.order}
desc

    limit #{page.offset}, #{page.pageSize}

select id, owner, title, description, teachers, courseTime, courseOver, infoPdf, prePdf, preVideo
from tb_course
where id = #{id}
